def test_1(instructions):
    return instructions.count('(') - instructions.count(')')


def test_2(instructions):
    current_floor = 0
    index = 1
    for char in instructions:
        current_floor += 1 if char is '(' else -1
        if current_floor < 0:
            return index
        index += 1

